mapper.select:
SELECT card_id, card_name, card_desc, card_img  
FROM card
WHERE card_id=#{VALUE}

SELECT card_id, card_name, card_desc, card_img_horizon, max_discount  
FROM card
WHERE card_id=#{VALUE}

mapper.update:
UPDATE CARD
SET	card_name = #{cardName}, card_desc = #{cardDesc}, max_discount = #{maxDiscount}
WHERE card_id = #{cardId}
